import React from 'react';
import SpeechRecognition from 'react-speech-recognition'
import $ from 'jquery';
import { PropTypes, Component } from 'react'
import logo from './logo.svg';
import './App.css';


class Dictaphone extends Component {
  constructor(props){
    super(props)

    this.state = {
      command   : '',
      a :1
    };

  }
  componentWillMount() {
    console.log("HERE")
    const {recognition} = this.props
    recognition.lang = 'zh-CN'
  }

  // function triggerIFTTT(){
  //   $.ajax({url:"https://maker.ifttt.com/trigger/ricecookeron/with/key/dyMeTmyKz4_uQNNPyqZABx",success:function(result){alert(result)}})
  // }


  render() {
    const { transcript, resetTranscript, browserSupportsSpeechRecognition, set} = this.props

    const setCommand = () => {

      var command = {transcript}['transcript']
      if (command === '打開電鍋'){
        console.log("set command get!")
        // $.ajax({url:"https://maker.ifttt.com/trigger/ricecookeron/with/key/dyMeTmyKz4_uQNNPyqZABx",success:function(result){alert(result)}})
        setTimeout(function() { {resetTranscript()}}, 30000);
      }
      else if (command === '關閉電鍋'){
        console.log("set command get!")
      //   $.ajax({url:"https://maker.ifttt.com/trigger/ricecookeroff/with/key/dyMeTmyKz4_uQNNPyqZABx",success:function(result){alert(result)}})
        setTimeout(function() { {resetTranscript()}}, 20000);
      }
      else{
        this.setState({
          a : command,
        })
        console.log(this.state.a)
        setTimeout(function() { {resetTranscript()}}, 15000);
      }

    }
    if (!browserSupportsSpeechRecognition) {
      return null
    }

    return (
        <div className="App">
          <header className="App-header">
            <img src={logo} className="App-logo" alt="logo" />
            <p>
              you can try it! 你好
            </p>
            <button onClick={resetTranscript}>Reset</button>
            <span>{setCommand()}</span>
            <span>{transcript}</span>
          </header>
        </div>
    )
  }



}

export default SpeechRecognition(Dictaphone)
