import React from 'react';
import SpeechRecognition from 'react-speech-recognition'
import { PropTypes, Component } from 'react'
import logo from './logo.svg';
import './App.css';


class Dictaphone extends Component {
  constructor(props){
    super(props)

    this.state = {
      command   : '',
    };

  }
  componentWillMount() {
    console.log("HERE")
    const {recognition} = this.props
    recognition.lang = 'zh-CN'
  }



  render() {
    const { transcript, resetTranscript, browserSupportsSpeechRecognition, set} = this.props

    const setCommand = () => {

      var command = {transcript}['transcript']
      if (command === '你好'){
        console.log("set command get!")
        // setTimeout(function() { {resetTranscript()}}, 10000);
      }
      else{
        console.log(command)
        setTimeout(function() { {resetTranscript()}}, 10000);
      }

    }
    if (!browserSupportsSpeechRecognition) {
      return null
    }

    return (
        <div className="App">
          <header className="App-header">
            <img src={logo} className="App-logo" alt="logo" />
            <p>
              you can try it! 你好
            </p>
            <button onClick={resetTranscript}>Reset</button>
            <span>{setCommand()}</span>
            <span>{transcript}</span>
          </header>
        </div>
    )
  }



}

export default SpeechRecognition(Dictaphone)
